void rec(int si,int sid,int i,int y,int z,int v,bool f=true){
    if(!f&&y<3&&z<3){
        if(i==0){
            single[sid][id[y][z]]+=v;
        }else if(i==3){
            if(sid==0||sid>=6){
                if(sid>=6)sid-=5;
                if(ok[sid]){
                    pw[0][rl[sid]][rl[id[y][z]+7]]+=v;
                }
            }
        }
        return;
    }
    for(auto p:{1,3}){
        vector<int> a{p,y,z};
        sort(a.begin(),a.end());
        int d=a[0];
        rec(si,sid,i+d,a[1]-d,a[2]-d,v,false);
    }
}

